  constructor(props) {
    super(props);
    this.state = {
        message: "SideBar Content, Click a Marker!"
    }
  }

  updateContent1 = () => {
      this.setState({ message: "Clicked Marker Test 1!" });
  }

  updateContent2 = () => {
    this.setState({ message: "Clicked Marker Test 2!"});
}

