import React from "react";
import { useState } from "react";
import "./Verticalmenutest.css";
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup'

// **** This is just a test, did not really work out ********

// changed from <li> to <nav> since the footer also uses <li> and they are aligned differently.


const Verticalmenu = (props) => {
  const words = [
    "Funding",
    "Pitch Competition",
    "Skills Building",
    "Coaching/Mentorship",
    "Social Justice",
    "Environmental",
    "Small Business",
    "Intrapreneurship",
    "Linked to Academics",
    "Networking",
    "Research",
    "Legal",
    "Community",
    "Events",
    "Competition",
    "Award"
  ];



  return (
    // <React.Fragment>
      <ButtonGroup size="m" vertical >
        <div className="Categories">
            <Button variant="outline-dark" onClick={() => {
              props.setKeyword(null);
              props.setKeynum(null);
            }}>
              Categories
            </Button>

        <div className="Verticalmenu">
          <h4
            className="top"
            onClick={() => {
              props.setKeynum(null);
              props.setKeyword(null);
            }}
          >
            Categories
          </h4>
          {words.map((word) => (
            <h1
              className="top"
              onClick={() => {
                props.setKeynum(null);
                if (props.currentKeyword === word) {
                  props.setKeyword(null);
                } else {
                  props.setKeyword(word);
                }
              }}
            >
              {word}
            </h1>
          ))}
        </div>
      </div>


    </ButtonGroup>
    // </React.Fragment>
  );

};


export default Verticalmenu;
