        onClick={this.moveStart} 
        //onMouseMove={this.whileMoving} 
        //onMouseUp = {this.moveEnd}
        onDrag
        onMouseLeave={this.moveEnd}
        src="/images/campus_map_half.png" alt="" />

      </div>
    </>


  );

}
