        //       props.setKeyword(null);
        //       props.setKeynum(null);
        //     }}>
        //       Categories
        //     </Button>
        // </div>

      <ButtonGroup size="m" vertical >
        <Button variant="outline-dark" className="menu-categories"
            onClick={() => {
              props.setKeynum(null);
              props.setKeyword(null);
            }}>
            Categories
        </Button>
        {words.map((word) => (
          <Button variant="outline-dark" className="menu-words"
            onClick={() => {
              props.setKeynum(null);
              if (props.currentKeyword === word) {
                props.setKeyword(null);
              } else {
                props.setKeyword(word);
              }
            }}
          >
            {word}
          </Button>
        ))}

        </ButtonGroup>

    // </React.Fragment>
  );

};


export default Verticalmenu;
