      //};
    };



    return (
      <div>
      <div class='Map' 
      onMouseDown= {moveStart}
      onMouseMove={whileMoves}
      onMouseUp={moveEnd}>
          <img style={styles}
          src="/images/campus_map_half.png" alt=""/>

      <div className='ImageFollow'
			//onMouseMove={handleMouseMove}
			//onMouseDown={handleMouseDown}
			//onMouseUp={handleMouseUp}
		>
		x:{styles.left} | y:{styles.top}

		<div className='msg' onClick={alertFunction(1)}
         style={{left: styles.left + 100, top: styles.top + 100}}>
			Marker1
		</div>

		<div className='always_half' onClick={alertFunction(2)}
         style={{left: styles.left + 100, top: styles.top + 100}}>
			Marker2
		</div>

	</div>
      </div>
      </div>

    );

  };

export default Map;


