import React from 'react';
import './Map.css';
import Zoom from './zoom';

const zoomStop = (zoomElement) => {
  
  let zoom = 1;

  document.addEventListener("wheel", function(e){
    
      zoomElement.style.transform = 1
      
      
    }


  
)};


export default zoomStop;
